import {useParams, useNavigate} from "react-router-dom"
import React, { useEffect, useState } from 'react';
import axios from "axios";

function EditProduct() {
    const {productId} = useParams();

    const [product, setProduct] = useState({
        id: '',
        name: '',
        price: '',
        quantity: '',
        sellerId: sessionStorage.getItem("userId"),
        categoryId: '',
      });
    
      const navigate = useNavigate();

      const handleInputChange = (event) => {
        let copyOfProduct = { ...product }
        copyOfProduct[event.target.name] = event.target.value;
        setProduct(copyOfProduct);
      };
    
      const handleSubmit = (event) => {
        console.log(product)
        event.preventDefault();
        axios.put("http://127.0.0.1:8080/product/editProduct", product)
            .then(res => {
                navigate("/seller/products")
            })
            .catch(err => {console.log(err)})
      };

      useEffect(() => {
        axios.get("http://127.0.0.1:8080/product/" + productId)
            .then(res => {
                console.log(res.data)
                setProduct(res.data)
            })
            .catch(err => console.log(err))
      }, [])

    return (
        <div className="container mt-5">
        <h2>Edit Product</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>ID:</label>
            <input
              type="text"
              disabled
              className="form-control"
              name="id"
              value={product.id}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              className="form-control"
              name="name"
              value={product.name}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Price:</label>
            <input
              type="number"
              className="form-control"
              name="price"
              value={product.price}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Quantity:</label>
            <input
              type="number"
              className="form-control"
              name="quantity"
              value={product.quantity}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <input
              type="number"
              className="form-control"
              name="categoryId"
              hidden
              value={product.categoryId}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit" style={{margin:"20px"}} className="btn btn-primary">Submit</button>
        </form>
      </div>
    )
}

export default EditProduct;
