import axios from 'axios';
import { Button } from 'bootstrap';
import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'

function AddProduct() {
  
  //
  const navigate = useNavigate();
  //
  const [product,setProduct] = useState({
                                productName: "",
                                price: 0,
                                quantity: 0,
                                sellerId: 0,
                                categoryId: 0
                              });

  
  //
  const addProd =()=>{
    axios.post("http://127.0.0.1:8080/product/addProduct",product).then(result =>{
      
    //
    console.log("product added successfully")
    
    //
      setProduct({
        productName: "",
        price: 0,
        quantity: 0,
        sellerId: 0,
        categoryId: 0
      })
    }).catch(error => {console.log(error)})

   
  }

  //
  const onInputChange = (event) => {
    let copyOfProduct = { ... product }
    copyOfProduct[event.target.name] = event.target.value;
    setProduct(copyOfProduct);
}
    return (    
 <div className='container'>
    <h3>Add Category</h3>
    <hr />
    <form>
    <div className='mb-3'>
              <label htmlFor=''>Name</label>
              <input
                type='text'
                className='form-control'
                placeholder='Enter name of Product here'
                
                name='productName'
                value={product.productName}
                onChange={onInputChange}
              />
      </div>
      <div className='mb-3'>
              <label htmlFor=''>Price</label>
              <input
                type='number'
                className='form-control'
                placeholder='Enter Price of Product here'

                name='price'
                value={product.price}
                onChange={onInputChange}
              />
      </div>
      <div className='mb-3'>
              <label htmlFor=''>Quantity</label>
              <input
                type='number'
                className='form-control'
                placeholder='Enter Quantity of product here'

                name='quantity'
                value={product.quantity}
                onChange={onInputChange}
              />
      </div>
      <div className='mb-3'>
              <label htmlFor=''>CategoryId</label>
              <input
                type='number'
                className='form-control'
                placeholder='Enter Category ID here'

                name='categoryId'
                value={product.categoryId}
                onChange={onInputChange}
              />
      </div>
      <div className='mb-3'>
              <label htmlFor=''>Seller ID</label>
              <input
                type='number'
                className='form-control'
                placeholder='Enter Seller Id here'

                name='sellerId'
                value={product.sellerId}
                onChange={onInputChange}
              />
      </div>
      <div>
        <button  className='btn btn-primary' onClick={addProd}>
          Save Or Update
        </button>
      </div>
    </form>
    <hr />
    {/* <Button onClick={()=>{navigate(/product)}}>Back to List</Button> */}
  </div> 
  );
}

export default AddProduct;
